const authReducer = (state = { authData: null }, action) => {
    switch (action.type) {
        case 'AUTH':
            localStorage.setItem('profile', JSON.stringify({ ...action?.data }));

            return { ...state, authData: action.data, loading: false, errors: null };

        case 'LOGOUT':
            localStorage.clear();
        default:
            return state;
    }
};

export default authReducer;
