import { makeStyles, StylesProvider } from '@material-ui/core/styles';


export default makeStyles({

    container: {
        background: 'linear-gradient(45deg, #fdbb2d, #22c1c3)'

    },

    image: {
        width: 400,
        height: 400,

    },

    topForm: {
        display: 'flex',
        flexDirection: 'row',
    },

    textField:
    {
        width: 700,
        marginTop: 10,
        height: 70

    },

    topRight: {
        marginLeft: 100,

    },

    description: {
        width: '99%',
        minHeight: 100,
        fontSize: 25,
        border: 'solid 1px black',
        background: 'transparent'

    },







});
