import { pink } from '@material-ui/core/colors';
import { makeStyles, StylesProvider } from '@material-ui/core/styles';


export default makeStyles({

    card: {
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        width: 400,
        float: 'right',
        padding: 20,


    },

    avatarContainer: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'

    },
    button: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    textField: {
        width: '200%'
    }



})
