import React, { useState } from 'react';

import { TextField, Typography, Paper, CardMedia, TextareaAutosize } from '@material-ui/core';
import useStyles from './styles';
import FileBase from 'react-file-base64';

const MomentForm = () => {
    const classes = useStyles();
    const [file, setFile] = useState();

    function handleChange(e) {
        console.log(e.target.files);
        //setFile(URL.createObjectURL(e.target.files[0]));
    }

    return (
        <container >
            <form autoComplete="off" noValidate onSubmit=''>
                <Paper className={classes.container}>
                    <div className={classes.topForm}>
                        <div>
                            <CardMedia className={classes.image} src={file} />
                        </div>
                        <div className={classes.topRight}>
                            <Typography className={classes.text} variant="h3" >Create a Moment </Typography>
                            <TextField
                                className={classes.textField}

                                required
                                id="title"
                                name="title"
                                label="Title"
                                variant="filled"
                            />

                            <TextField
                                className={classes.textField}
                                name="location"
                                required
                                id="location"
                                label="Location"
                                variant="filled"
                            />

                            <TextField
                                className={classes.textField}
                                name="tags"
                                required
                                id="tags"
                                label="Tags"
                                variant="filled"
                            />

                            <TextField
                                className={classes.textField}
                                name="date"
                                required
                                id="date"
                                variant="filled"
                                type='date'

                            />
                            <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setFile(base64)} /></div>
                            {console.log(file)}
                        </div>
                    </div>
                    <div>
                        <TextareaAutosize
                            className={classes.description}
                            cols="40"
                            row="5"
                            required
                            id="description"
                            name="description"
                            placeholder="Description"
                        />
                    </div>

                </Paper>
            </form>
        </container>
    );
}
export default MomentForm;
