import { makeStyles, StylesProvider } from '@material-ui/core/styles';


export default makeStyles({

    container: {
        marginTop: 40,

    },

    imageContainer: {
        display: 'flex',
        justifyContent: 'center',
        marginLeft: 40
    },

    image: {
        position: 'relative',
        width: 400,
        height: 400,
        border: 'solid',
        justifyContent: 'right',

    }

});
